import React from "react";
import { useCollection } from '../hooks/useCollection'

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="user-list bg-light p-3">
            <h2 className="text-center mb-4">All Users</h2>
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && (
                <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">
                    {documents.map(user => (
                        <div key={user.id} className="col">
                            <div className="card h-100">
                                <div className="card-body d-flex align-items-center">
                                    {user.photoURL && <Avatar src={user.photoURL} className="me-3" style={{ width: '40px', height: '40px' }} />}
                                    {!user.photoURL && <i className="bi bi-person-circle me-3" style={{ fontSize: '40px' }}></i>}
                                    <span>{user.displayName}</span>
                                </div>
                                {user.online && (
                                    <div className="card-footer text-end">
                                        <span className="online-user me-2"></span>
                                        <span>Online</span>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    )


}
