import React, { useState } from "react";
import { useCollection } from "../hooks/useCollection";
import Avatar from "./Avatar";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUsers } from "@fortawesome/free-solid-svg-icons";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection("users");
    const [showUsers, setShowUsers] = useState(false);

    const toggleUsers = () => {
        setShowUsers(!showUsers);
    };

    return (
        <>
            <Navbar bg="light" expand="lg" className="d-none d-lg-block">
                <Nav className="shadow-lg p-2 mt-4 rounded d-flex flex-column">
                    {isPending && <Nav.Item>Loading users...</Nav.Item>}
                    {error && <Nav.Item>{error}</Nav.Item>}
                    {documents &&
                        documents.map((user) => (
                            <Nav.Item key={user.id} className="shadow-lg p-2 rounded d-flex justify-content-center align-items-center flex-column">
                                <Avatar src={user.photoURL} style={{ width: "30px", height: "30px" }} />
                                <span className="ml-2">{user.displayName}</span>
                                {user.online && (
                                    <span
                                        className="d-inline-block rounded-circle ml-2"
                                        style={{ width: "10px", height: "10px", backgroundColor: "#25ae5f" }}
                                    ></span>
                                )}
                            </Nav.Item>
                        ))}
                </Nav>
            </Navbar>

            <div className="d-block d-lg-none bg-light px-3">
                <button type="button" className="btn btn-primary d-block" onClick={toggleUsers}>
                    <FontAwesomeIcon icon={faUsers} />
                </button>
                {showUsers && (
                    <div className="mt-4">
                        {isPending && <div>Loading users...</div>}
                        {error && <div>{error}</div>}
                        {documents &&
                            documents.map((user) => (
                                <ul key={user.id} className="shadow-lg p-2 rounded">
                                    <li className="d-flex justify-content-center align-items-center flex-column">
                                        <Avatar src={user.photoURL} style={{ width: "30px", height: "30px" }} />
                                        <div>
                                            <span className="p-1">{user.displayName}</span>
                                            {user.online && (
                                                <span
                                                    className="d-inline-block rounded-circle"
                                                    style={{ width: "10px", height: "10px", backgroundColor: "#25ae5f" }}
                                                ></span>
                                            )}
                                        </div>
                                    </li>
                                </ul>
                            ))}
                    </div>
                )}
            </div>
        </>
    );
}
