import React, { useState } from "react";
import { useCollection } from "../hooks/useCollection";
import Avatar from "./Avatar";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUsers } from "@fortawesome/free-solid-svg-icons";

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection("users");
    const [isExpanded, setIsExpanded] = useState(false);

    const toggleMenu = () => {
        setIsExpanded(!isExpanded);
    };

    return (
        <div className="d-block d-lg-none bg-light px-3">
            <button
                className="btn btn-link"
                onClick={toggleMenu}
                aria-expanded={isExpanded}
            >
                <FontAwesomeIcon icon={faUsers} />
            </button>
            {isExpanded && (
                <div className="mt-4">
                    {isPending && <div>Loading users...</div>}
                    {error && <div>{error}</div>}
                    {documents && (
                        <>
                            {documents.map((user) => (
                                <ul key={user.id} className="shadow-lg p-2 rounded">
                                    <li className="d-flex justify-content-center align-items-center flex-column">
                                        <Avatar
                                            src={user.photoURL}
                                            style={{ width: "30px", height: "30px" }}
                                        />
                                        <div>
                                            <span className="p-1">{user.displayName}</span>
                                            {user.online && (
                                                <span
                                                    className="d-inline-block rounded-circle"
                                                    style={{
                                                        width: "10px",
                                                        height: "10px",
                                                        backgroundColor: "#25ae5f",
                                                    }}
                                                ></span>
                                            )}
                                        </div>
                                    </li>
                                </ul>
                            ))}
                        </>
                    )}
                </div>
            )}
        </div>
    );
}
