import React from "react";
import { useCollection } from '../hooks/useCollection'

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="bg-light px-3 py-1 d-flex justify-content-center">
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && (
                <div className="mt-4">
                    {documents.map(user => (
                        <ul key={user.id}>
                            <li className="d-flex align-items-center flex-wrap shadow-sm p-2">
                                <Avatar
                                    src={user.photoURL}
                                    className="me-2 p-2"
                                    style={{ width: '40px', height: '40px' }}
                                />
                                <span className="p-2">{user.displayName}</span>
                                {user.online && (
                                    <span
                                        className="d-inline-block rounded-circle ms-auto mt-2 mt-sm-0"
                                        style={{ width: '12px', height: '12px', backgroundColor: "#25ae5f" }}
                                    ></span>
                                )}
                            </li>
                        </ul>
                    ))}
                </div>
            )}
        </div>
    );

}
