import React from "react";
import { useCollection } from '../hooks/useCollection'

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="user-list bg-light p-3">
            <h2 className="text-center mb-4">All Users</h2>
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && documents.map(user => (
                <div key={user.id} className="user-list-item d-flex justify-content-center align-items-center my-3">
                    {user.online && <span className="online-user me-2"></span>}
                    <span className="mx-2">{user.displayName}</span>
                    <Avatar src={user.photoURL} className="ms-2" style={{ width: '40px', height: '40px' }} />
                </div>
            ))}
        </div>
    )

}
