import React from "react";
import { useCollection } from '../hooks/useCollection'
import { ListGroup } from 'react-bootstrap';

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="bg-light px-3 py-1 d-flex justify-content-center">
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && (
                <div className="mt-4">
                    {documents.map(user => (
                        <ul key={user.id} className="d-flex align-items-center flex-wrap shadow-sm">
                            <li className="d-flex align-items-center me-auto">
                                <Avatar src={user.photoURL} className="me-2" style={{ width: '40px', height: '40px' }} />
                                <span className="p-2">{user.displayName}</span>
                            </li>
                            {user.online && <span className="d-inline-block rounded-circle ms-auto mt-2 mt-sm-0" style={{ width: '12px', height: '12px', backgroundColor: "#25ae5f"}}></span>}
                        </ul>
                    ))}
                </div>
            )}
        </div>
    )
}
