import React from "react";
import { useCollection } from '../hooks/useCollection'
import { ListGroup } from 'react-bootstrap';

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="user-list bg-light px-3 py-1 d-flex justify-content-center">
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && (
                <ListGroup className="mt-4">
                    {documents.map(user => (
                        <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center shadow-sm">
                            <div className="d-flex align-items-center">
                                <Avatar src={user.photoURL} className="me-2" style={{ width: '40px', height: '40px' }} />
                                <span className="p-2">{user.displayName}</span>
                                {user.online && <span className="d-inline-block me-2 bg-success rounded-circle" style={{ width: '12px', height: '12px'}}></span>}
                            </div>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            )}
        </div>
    )
}
