import React from 'react';
import logo from '../assets/logo.png';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useLogout } from '../hooks/useLogout'
import { useAuthContext } from '../hooks/useAuthContext'

function Header() {

    const { logout, isPending } = useLogout()
    const { user } = useAuthContext()

    return (
        <Navbar expand="lg" className="shadow-sm" style={{backgroundColor: "#e1d0f8", height: "60px"}}>
            <Container>

                <Navbar.Brand href="#home">
                    <img
                        src={logo}
                        height="30"
                        className="d-inline-block align-top"
                        alt="ProjectTrek logo"
                    />
                    {' ProjectTrek'}
                </Navbar.Brand>

                <Navbar.Toggle aria-controls="basic-navbar-nav" />

                <Navbar.Collapse id="basic-navbar-nav" className="text-end mt-2">
                    <Nav className="ms-auto" style={{width: 300}}>
                        {!user && ( <Link to="/login" className="nav-link">Login</Link>)}
                        <Link to="/signup" className="nav-link">Signup</Link>
                         {!isPending && <button className="btn border-0 ml-auto" onClick={logout}>Logout</button>}
                         {isPending && <button className="btn border-0 ml-auto" disabled>Logging out...</button>}
                    </Nav>
                </Navbar.Collapse>

            </Container>
        </Navbar>
    )
}

export default Header;

