import React from "react";
import { useCollection } from '../hooks/useCollection'
import { Navbar, Nav, Container } from 'react-bootstrap';
// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className=" d-none d-lg-block bg-light px-3">
            <Navbar expand="lg">
                <Container>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" className="p-0 mt-1" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        {isPending && <div>Loading users...</div>}
                        {error && <div>{error}</div>}
                        {documents && (
                            <Nav className="flex-column mt-4">
                                {documents.map(user => (
                                    <Nav.Link key={user.id} className="shadow-lg p-2 rounded">
                                        <li className="d-flex justify-content-center align-items-center flex-column">
                                            <Avatar src={user.photoURL} style={{ width: '30px', height: '30px' }} />
                                            <div>
                                                <span className="p-1">{user.displayName}</span>
                                                {user.online && <span className="d-inline-block rounded-circle" style={{ width: '10px', height: '10px', backgroundColor: "#25ae5f"}}></span>}
                                            </div>
                                        </li>
                                    </Nav.Link>
                                ))}
                            </Nav>
                        )}
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </div>
    )
}
