import React from "react";
import { useCollection } from '../hooks/useCollection'
import { ListGroup, Navbar, Container } from 'react-bootstrap';

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="user-list bg-light">
            <Navbar bg="light" expand="md">
                <Navbar.Brand>All Users</Navbar.Brand>
                <Navbar.Toggle aria-controls="user-list" />
                <Navbar.Collapse id="user-list">
                    <ListGroup className="flex-wrap">
                        {documents && documents.map(user => (
                            <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center">
                                <div className="d-flex flex-column align-items-center">
                                    {user.online && <span className="online-user"></span>}
                                    <Avatar src={user.photoURL} className="mt-2" style={{ width: '30px', height: '30px' }} />
                                </div>
                                <div className="d-none d-md-flex align-items-center">
                                    {user.displayName}
                                </div>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                </Navbar.Collapse>
            </Navbar>
        </div>
    )
}
