import React, { useState } from "react";
import { useCollection } from "../hooks/useCollection";
import { Button, ListGroup, OverlayTrigger, Popover } from "react-bootstrap";
import Avatar from "./Avatar";

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection("users");
    const [showUsers, setShowUsers] = useState(false);

    const toggleShowUsers = () => setShowUsers(!showUsers);

    const popover = (
        <Popover id="users-popover">
            <Popover.Header as="h3">Users</Popover.Header>
            <Popover.Body>
                <ListGroup>
                    {documents &&
                        documents.map((user) => (
                            <ListGroup.Item key={user.id}>
                                <div className="d-flex align-items-center">
                                    <Avatar
                                        src={user.photoURL}
                                        style={{ width: "30px", height: "30px" }}
                                    />
                                    <span className="ml-3">{user.displayName}</span>
                                    {user.online && (
                                        <span
                                            className="d-inline-block rounded-circle ml-2"
                                            style={{
                                                width: "10px",
                                                height: "10px",
                                                backgroundColor: "#25ae5f",
                                            }}
                                        ></span>
                                    )}
                                </div>
                            </ListGroup.Item>
                        ))}
                </ListGroup>
            </Popover.Body>
        </Popover>
    );

    return (
        <>
            <div className=" d-none d-lg-block bg-light px-3">
                {isPending && <div>Loading users...</div>}
                {error && <div>{error}</div>}
                {documents && (
                    <div className="mt-4">
                        {documents.map((user) => (
                            <ul key={user.id} className="shadow-lg p-2 rounded">
                                <li className="d-flex justify-content-center align-items-center flex-column">
                                    <Avatar
                                        src={user.photoURL}
                                        style={{ width: "30px", height: "30px" }}
                                    />
                                    <div>
                                        <span className="p-1">{user.displayName}</span>
                                        {user.online && (
                                            <span
                                                className="d-inline-block rounded-circle"
                                                style={{
                                                    width: "10px",
                                                    height: "10px",
                                                    backgroundColor: "#25ae5f",
                                                }}
                                            ></span>
                                        )}
                                    </div>
                                </li>
                            </ul>
                        ))}
                    </div>
                )}
            </div>

            {/* Button to toggle user list on small screens */}
            <div className="d-lg-none d-flex justify-content-end mt-3">
                <OverlayTrigger
                    trigger="click"
                    placement="top"
                    overlay={popover}
                    show={showUsers}
                    rootClose
                    onHide={() => setShowUsers(false)}
                >
                    <Button variant="secondary" onClick={toggleShowUsers}>
                        Users
                    </Button>
                </OverlayTrigger>
            </div>
        </>
    );
}
