import React from "react";
import { useCollection } from '../hooks/useCollection'
import { ListGroup, Row, Col } from 'react-bootstrap';

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')

    return (
        <div className="user-list bg-light p-3">
            <h2 className="text-end border-bottom pb-2 mb-4 fs-4">All Users</h2>
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && (
                <Row>
                    <Col lg={8}>
                        <ListGroup>
                            {documents.map(user => (
                                <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center">
                                    <div className="d-flex align-items-center">
                                        {user.online && <span className="online-user me-2"></span>}
                                        <Avatar src={user.photoURL} className="me-2" style={{ width: '40px', height: '40px' }} />
                                        <span className="p-2">{user.displayName}</span>
                                    </div>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    </Col>
                    <Col lg={4} className="mt-4 mt-lg-0">
                        <h4 className="text-center mb-4">Online Users</h4>
                        <ListGroup>
                            {documents.filter(user => user.online).map(user => (
                                <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center">
                                    <div className="d-flex align-items-center">
                                        <Avatar src={user.photoURL} className="me-2" style={{ width: '40px', height: '40px' }} />
                                        <span className="p-2">{user.displayName}</span>
                                    </div>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    </Col>
                </Row>
            )}
        </div>
    )
}
