import React, { useState, useEffect } from "react";
import { useCollection } from '../hooks/useCollection'
import { ListGroup } from 'react-bootstrap';

// components
import Avatar from './Avatar'

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users')
    const [isMobileView, setIsMobileView] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth <= 576) {
                setIsMobileView(true);
            } else {
                setIsMobileView(false);
            }
        }
        handleResize();
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, []);

    return (
        <div className="user-list bg-light p-3">
            <h2 className="text-end border-bottom pb-2 mb-4 fs-4">All Users</h2>
            {isPending && <div>Loading users...</div>}
            {error && <div>{error}</div>}
            {documents && (
                <ListGroup className={isMobileView ? "mobile-view" : ""}>
                    {documents.map(user => (
                        <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center">
                            <div className="d-flex align-items-center">
                                {user.online && <span className="online-user me-2"></span>}
                                <Avatar src={user.photoURL} className="me-2" style={{ width: isMobileView ? '30px' : '40px', height: isMobileView ? '30px' : '40px' }} />
                                {isMobileView && <span className="p-2">{user.displayName}</span>}
                            </div>
                            {!isMobileView && <span className="p-2">{user.displayName}</span>}
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            )}
        </div>
    )
}
