import React, { useState } from 'react';
import { useCollection } from '../hooks/useCollection';
import { Navbar, Nav, Button, ListGroup } from 'react-bootstrap';
import Avatar from './Avatar';

export default function ActiveUsers() {
    const { isPending, error, documents } = useCollection('users');
    const [expanded, setExpanded] = useState(false);

    return (
        <div className="user-list bg-light p-3">
            <Navbar expand="sm">
                <Navbar.Brand>All Users</Navbar.Brand>
                <Navbar.Toggle onClick={() => setExpanded(!expanded)} aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <ListGroup className="d-block d-sm-none">
                            {documents && documents.map((user) => (
                                <ListGroup.Item key={user.id} className="d-flex align-items-center">
                                    <Avatar src={user.photoURL} className="me-2" style={{ width: '40px', height: '40px' }} />
                                    <span>{user.displayName}</span>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                        <ListGroup className="d-none d-sm-block">
                            {documents && documents.map((user) => (
                                <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center">
                                    <div className="d-flex align-items-center">
                                        {user.online && <span className="online-user me-2"></span>}
                                        <Avatar src={user.photoURL} className="me-2" style={{ width: '40px', height: '40px' }} />
                                        <span className="p-2">{user.displayName}</span>
                                    </div>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        </div>
    );
}
