import React from 'react';
import logo from '../assets/logo.png';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useLogout } from '../hooks/useLogout'
import { useAuthContext } from '../hooks/useAuthContext'

export default function Header() {

    const { logout, isPending } = useLogout()
    const { user } = useAuthContext()

    return (
        <Navbar expand="lg" className="navbar shadow-sm" style={{backgroundColor: "#e1d0f8", height: "60px"}}>
            <Container>
                <Navbar.Header>
                <Navbar.Brand>
                    <Link to="/">
                    <img
                        src={logo}
                        height="30"
                        className="d-inline-block align-top"
                        alt="ProjectTrek logo"
                    />
                    {' ProjectTrek'}
                    </Link>
                </Navbar.Brand>
                    <Navbar.Toggle />
                </Navbar.Header>
                <Navbar.Collapse>
                    <Nav pullRight>
                        {!user && ( <Link to="/login" className="nav-link">Login</Link> )}
                        {!user && ( <Link to="/signup" className="nav-link">Signup</Link> )}
                        {!user && (
                            <>
                            {!isPending && <button className="btn btn-transparent border-0 text-end p-0 mr-0" onClick={logout}>Logout</button>}
                            {isPending && <button className="btn btn-transparent border-0 text-end p-0 mr-0" disabled>Logging out...</button>}
                         </>)}

                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}


